function Todos({ todos }) {
  // Message for if todos are loading
  if (!isLoaded(todos)) {
    return Loading...
  }

  // Message if todos are empty
  if (isEmpty(todos)) {
    return No Todos Found
  }

  return {JSON.stringify(todos, null, 2)}
}

